import firebase from 'firebase'
import db from '../db'
  
  export default {
    name: 'enlist',
    data () {
      return {
        events: [],
        loading: true,
        isActive: false,
        user_id: '',
        user_events:[],
        user_events_recordlist:[]
      }
    },
    methods:{
        enlist(id){
            console.log(id)
            console.log(event.target)
          
           if(event.target.innerText === 'Enlist'){
                event.target.innerText = 'Enlisted'
           }else{
               event.target.innerText = 'Enlist'
           } 

           db.collection('users').where('user_id', '==', this.user_id).get().then((querySnapshot) => {
          querySnapshot.forEach((doc) => {
            doc.ref.update({
               user_events: firebase.firestore.FieldValue.arrayUnion(id),
            })
            .then(() => {
              console.log('enlisted')
            });
          })
        })
        },
        showEnlistedEvents(user_events_recordlist){
            console.log(user_events_recordlist)
            db.collection('events').where('event_id', '==', this.user_id).get().then((querySnapshot) => {
                querySnapshot.forEach((doc) => {
                const data = {
                    'name': doc.data().name,
                }
                this.user_events.push(data)
                
                })
            })
        }
    },
    created () {
          
    this.user_id = firebase.auth().currentUser.uid;

      db.collection('events').get().then((querySnapshot) => {
          
        this.loading = false
        querySnapshot.forEach((doc) => {
          const data = {
            'id': doc.id,
            'event_id': doc.data().id,
            'name': doc.data().name,
            'descr': doc.data().descr,
            'start': doc.data().start,
            'end': doc.data().end,
            'status': doc.data().status
          }
          this.events.push(data)
          
        })
      })

 
      db.collection('users').where('user_id', '==', this.user_id).get().then((querySnapshot) => {
          this.loading = false
            console.log('this.user_id ' +  this.user_id)
          querySnapshot.forEach((doc) => {            
            const data = {
                'user_events': doc.data().user_events
            }
            
            this.user_events_recordlist.push(data['user_events'])
          })
        })
         // console.log(this.user_events_recordlist)
  
      
    },
   
  }
